import React, { useState, useEffect } from 'react';

const AISmartMatch = ({ userId, token }) => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [matches, setMatches] = useState([]);
  const [loadingMatches, setLoadingMatches] = useState(false);
  const [loadingAnswer, setLoadingAnswer] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (userId && token) {
      fetchSmartMatches();
    }
  }, [userId, token]);

  const fetchSmartMatches = async () => {
    setLoadingMatches(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5003/api/ai/smart-match', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch smart matches');
      }
      const data = await response.json();
      setMatches(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoadingMatches(false);
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;
    setLoadingAnswer(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5003/api/ai/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ question }),
      });
      if (!response.ok) {
        throw new Error('Failed to get AI answer');
      }
      const data = await response.json();
      setAnswer(data.answer);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoadingAnswer(false);
    }
  };

  return (
    <div className="bg-gradient-to-r from-blue-50 to-indigo-100 p-6 rounded-xl shadow-lg mt-6 border border-blue-200">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">AI Smart Match & Assistant</h2>

      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-3 text-gray-700">Smart Match Suggestions</h3>
        {loadingMatches ? (
          <div className="flex items-center space-x-2">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
            <p className="text-gray-600">Loading matches...</p>
          </div>
        ) : error ? (
          <p className="text-red-500 bg-red-50 p-3 rounded-lg border border-red-200">{error}</p>
        ) : matches.length === 0 ? (
          <p className="text-gray-500 bg-gray-50 p-3 rounded-lg">No smart matches found.</p>
        ) : (
          <div className="bg-white p-4 rounded-lg shadow-md max-h-48 overflow-auto border border-gray-200">
            <ul className="space-y-3">
              {matches.map((match, index) => (
                <li key={index} className="p-3 bg-gradient-to-r from-green-50 to-green-100 rounded-lg border border-green-200">
                  <div className="font-medium text-green-800">Delivery: <strong>{match.deliveryRequest.title}</strong></div>
                  <div className="text-sm text-green-700">({match.deliveryRequest.from} → {match.deliveryRequest.to})</div>
                  <div className="text-sm text-green-600 mt-1">Matched Trip: <strong>{match.travelerTrip.from} → {match.travelerTrip.to}</strong></div>
                  <div className="text-xs text-green-500">on {new Date(match.travelerTrip.date).toLocaleDateString()}</div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <form onSubmit={handleAskQuestion} className="mb-6">
        <label htmlFor="question" className="block text-lg font-semibold mb-2 text-gray-700">Ask AI a question about your trip or the app:</label>
        <div className="relative">
          <input
            id="question"
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent shadow-sm"
            placeholder="Type your question here..."
          />
        </div>
        <button
          type="submit"
          className="mt-3 bg-gradient-to-r from-blue-600 to-blue-700 text-white px-6 py-2 rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all duration-200 shadow-md disabled:opacity-50 disabled:cursor-not-allowed"
          disabled={loadingAnswer}
        >
          {loadingAnswer ? (
            <div className="flex items-center space-x-2">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
              <span>Thinking...</span>
            </div>
          ) : (
            'Ask AI'
          )}
        </button>
      </form>

      {answer && (
        <div className="bg-gradient-to-r from-gray-50 to-gray-100 p-4 rounded-lg shadow-md border border-gray-200">
          <h4 className="text-lg font-semibold mb-3 text-gray-800">AI Answer:</h4>
          <p className="text-gray-700 leading-relaxed">{answer}</p>
        </div>
      )}
    </div>
  );
};

export default AISmartMatch;
