import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBox, FaTruck, FaBoxOpen, FaClock, FaPlane, FaMapMarkerAlt, FaPlus, FaWallet, FaExclamationTriangle, FaStar, FaCog, FaBell } from 'react-icons/fa';
import AISmartMatch from '../components/AISmartMatch';
import Notifications from '../components/Notifications';
import ThemeToggle from '../components/ThemeToggle';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [deliveryRequests, setDeliveryRequests] = useState([]);
  const [travelerTrips, setTravelerTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(storedUser));
    fetchDeliveryRequests();
    if (JSON.parse(storedUser).role === 'Traveler') {
      fetchTravelerTrips();
    }
  }, []);

  const fetchDeliveryRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/delivery', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch delivery requests');
      }
      const data = await response.json();
      setDeliveryRequests(data);
    } catch (error) {
      console.error(error);
      setDeliveryRequests([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchTravelerTrips = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/traveler', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch traveler trips');
      }
      const data = await response.json();
      setTravelerTrips(data);
    } catch (error) {
      console.error(error);
    }
  };

  const countByStatus = (status) => {
    return deliveryRequests.filter((req) => req.status === status).length;
  };

  const totalRequests = deliveryRequests.length;
  const inTransit = countByStatus('In Transit');
  const delivered = countByStatus('Delivered');
  const pending = countByStatus('Pending');

  const isTraveler = user && user.role === 'Traveler';
  const isSender = user && user.role === 'Sender';

  // Sidebar navigation items
  const sidebarItems = [
    { label: 'Dashboard', icon: <FaBox />, onClick: () => navigate('/dashboard') },
    { label: 'Wallet', icon: <FaWallet />, onClick: () => navigate('/wallet') },
    { label: 'Reviews', icon: <FaStar />, onClick: () => navigate('/reviews/' + (user ? user.id : '')) },
    { label: 'Fraud Reports', icon: <FaExclamationTriangle />, onClick: () => navigate('/admin/fraud-reports') },
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-md p-6">
        <h2 className="text-xl font-bold mb-6">Easy Delivery</h2>
        <nav className="space-y-4">
          {sidebarItems.map((item) => (
            <button
              key={item.label}
              onClick={item.onClick}
              className="flex items-center space-x-3 w-full text-left px-3 py-2 rounded hover:bg-gray-100"
            >
              <span className="text-lg">{item.icon}</span>
              <span>{item.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6">
        {/* Role-based Header */}
        <div className="bg-blue-700 text-white rounded-lg p-6 mb-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold">
                Welcome back, {user ? user.name : 'User'}!
              </h1>
              <p>
                {isSender && "Ready to send packages and track deliveries?"}
                {isTraveler && "Ready to post trips and help deliver packages?"}
              </p>
            </div>
            <div className="flex space-x-3">
              {isSender && (
                <button
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded flex items-center"
                  onClick={() => navigate('/new-delivery')}
                >
                  <FaPlus className="mr-2" />
                  New Delivery
                </button>
              )}
              {isTraveler && (
                <button
                  className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded flex items-center"
                  onClick={() => navigate('/post-trip')}
                >
                  <FaPlane className="mr-2" />
                  Post Trip
                </button>
              )}
              <button
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded flex items-center"
                onClick={() => {
                  localStorage.removeItem('token');
                  localStorage.removeItem('user');
                  navigate('/login');
                }}
              >
                Logout
              </button>
              <input
                type="text"
                placeholder="Search..."
                className="p-2 rounded text-black"
              />
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex items-center">
              <FaBox className="text-blue-500 text-2xl mr-3" />
              <div>
                <p className="text-gray-600">Total Requests</p>
                <p className="text-2xl font-bold">{totalRequests}</p>
              </div>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex items-center">
              <FaClock className="text-yellow-500 text-2xl mr-3" />
              <div>
                <p className="text-gray-600">Pending</p>
                <p className="text-2xl font-bold">{pending}</p>
              </div>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex items-center">
              <FaTruck className="text-orange-500 text-2xl mr-3" />
              <div>
                <p className="text-gray-600">In Transit</p>
                <p className="text-2xl font-bold">{inTransit}</p>
              </div>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex items-center">
              <FaBoxOpen className="text-green-500 text-2xl mr-3" />
              <div>
                <p className="text-gray-600">Delivered</p>
                <p className="text-2xl font-bold">{delivered}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Deliveries/Trips */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
          {isSender && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-xl font-bold mb-4">Recent Deliveries</h2>
              {loading ? (
                <p>Loading...</p>
              ) : deliveryRequests.length === 0 ? (
                <p>No delivery requests yet.</p>
              ) : (
                <div className="space-y-3">
                  {deliveryRequests.slice(0, 5).map((req) => (
                    <div key={req._id} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                      <div>
                        <p className="font-semibold">{req.title}</p>
                        <p className="text-sm text-gray-600">{req.from} → {req.to}</p>
                      </div>
                      <span className={`px-2 py-1 rounded text-xs font-semibold ${
                        req.status === 'Delivered' ? 'bg-green-100 text-green-800' :
                        req.status === 'In Transit' ? 'bg-blue-100 text-blue-800' :
                        'bg-yellow-100 text-yellow-800'
                      }`}>
                        {req.status}
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {isTraveler && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-xl font-bold mb-4">Recent Trips</h2>
              {travelerTrips.length === 0 ? (
                <p>No trips posted yet.</p>
              ) : (
                <div className="space-y-3">
                  {travelerTrips.slice(0, 5).map((trip) => (
                    <div key={trip._id} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                      <div>
                        <p className="font-semibold">{trip.from} → {trip.to}</p>
                        <p className="text-sm text-gray-600">{new Date(trip.date).toLocaleDateString()}</p>
                      </div>
                      <span className="px-2 py-1 rounded text-xs font-semibold bg-green-100 text-green-800">
                        Active
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>

        {/* AI Smart Match */}
        <AISmartMatch userId={user ? user.id : null} token={localStorage.getItem('token')} />

        {/* Notifications */}
        <Notifications />
      </div>
    </div>
  );
};

export default Dashboard;
