import React, { useState, useEffect } from 'react';

const AISmartMatch = ({ userId, token }) => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [matches, setMatches] = useState([]);
  const [loadingMatches, setLoadingMatches] = useState(false);
  const [loadingAnswer, setLoadingAnswer] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (userId && token) {
      fetchSmartMatches();
    }
  }, [userId, token]);

  const fetchSmartMatches = async () => {
    setLoadingMatches(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5003/api/ai/smart-match', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch smart matches');
      }
      const data = await response.json();
      setMatches(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoadingMatches(false);
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;
    setLoadingAnswer(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5003/api/ai/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ question }),
      });
      if (!response.ok) {
        throw new Error('Failed to get AI answer');
      }
      const data = await response.json();
      setAnswer(data.answer);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoadingAnswer(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded shadow mt-6">
      <h2 className="text-xl font-bold mb-4">AI Smart Match & Assistant</h2>

      <div className="mb-4">
        <h3 className="font-semibold mb-2">Smart Match Suggestions</h3>
        {loadingMatches ? (
          <p>Loading matches...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : matches.length === 0 ? (
          <p>No smart matches found.</p>
        ) : (
          <ul className="list-disc list-inside max-h-48 overflow-auto">
            {matches.map((match, index) => (
              <li key={index} className="mb-2">
                Delivery: <strong>{match.deliveryRequest.title}</strong> ({match.deliveryRequest.from} → {match.deliveryRequest.to})<br />
                Matched Trip: <strong>{match.travelerTrip.from} → {match.travelerTrip.to}</strong> on {new Date(match.travelerTrip.date).toLocaleDateString()}
              </li>
            ))}
          </ul>
        )}
      </div>

      <form onSubmit={handleAskQuestion} className="mb-4">
        <label htmlFor="question" className="block font-semibold mb-1">Ask AI a question about your trip or the app:</label>
        <input
          id="question"
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          className="w-full p-2 border rounded mb-2"
          placeholder="Type your question here..."
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
          disabled={loadingAnswer}
        >
          {loadingAnswer ? 'Thinking...' : 'Ask'}
        </button>
      </form>

      {answer && (
        <div className="bg-gray-100 p-4 rounded">
          <h4 className="font-semibold mb-2">AI Answer:</h4>
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
};

export default AISmartMatch;
