import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBox, FaTruck, FaBoxOpen, FaClock, FaPlane, FaMapMarkerAlt, FaPlus, FaWallet, FaExclamationTriangle, FaStar, FaCog, FaBell, FaSearch, FaUser, FaSignOutAlt, FaRoute, FaChartBar, FaQuestionCircle } from 'react-icons/fa';
import AISmartMatch from '../components/AISmartMatch';
import Notifications from '../components/Notifications';
import ThemeToggle from '../components/ThemeToggle';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [deliveryRequests, setDeliveryRequests] = useState([]);
  const [travelerTrips, setTravelerTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(storedUser));
    fetchDeliveryRequests();
    if (JSON.parse(storedUser).role === 'Traveler') {
      fetchTravelerTrips();
    }
  }, []);

  const fetchDeliveryRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/delivery', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch delivery requests');
      }
      const data = await response.json();
      setDeliveryRequests(data);
    } catch (error) {
      console.error(error);
      setDeliveryRequests([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchTravelerTrips = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/traveler', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch traveler trips');
      }
      const data = await response.json();
      setTravelerTrips(data);
    } catch (error) {
      console.error(error);
    }
  };

  const countByStatus = (status) => {
    return deliveryRequests.filter((req) => req.status === status).length;
  };

  const totalRequests = deliveryRequests.length;
  const inTransit = countByStatus('In Transit');
  const delivered = countByStatus('Delivered');
  const pending = countByStatus('Pending');

  const isTraveler = user && user.role === 'Traveler';
  const isSender = user && user.role === 'Sender';

  // Sidebar navigation items
  const sidebarItems = [
    { label: 'Dashboard', icon: <FaBox />, onClick: () => navigate('/dashboard') },
    { label: 'Wallet', icon: <FaWallet />, onClick: () => navigate('/wallet') },
    { label: 'Reviews', icon: <FaStar />, onClick: () => navigate('/reviews/' + (user ? user.id : '')) },
    { label: 'Messages', icon: <FaBell />, onClick: () => navigate('/messages') },
    { label: 'All Deliveries', icon: <FaTruck />, onClick: () => navigate('/deliveries') },
    { label: 'Delivery History', icon: <FaClock />, onClick: () => navigate('/history') },
    { label: 'Post Trip', icon: <FaPlane />, onClick: () => navigate('/post-trip') },
    { label: 'Analytics', icon: <FaChartBar />, onClick: () => alert('Analytics feature coming soon!') },
    { label: 'Settings', icon: <FaCog />, onClick: () => alert('Settings feature coming soon!') },
    { label: 'Support', icon: <FaQuestionCircle />, onClick: () => alert('Support feature coming soon!') },
    { label: 'Fraud Reports', icon: <FaExclamationTriangle />, onClick: () => navigate('/admin/fraud-reports') },
    { label: 'Admin Dashboard', icon: <FaCog />, onClick: () => navigate('/admin') },
    { label: 'Delivery Tracking', icon: <FaMapMarkerAlt />, onClick: () => navigate('/delivery-tracking') },
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-64 bg-white dark:bg-secondary-800 shadow-md p-6 rounded-2xl">
        <h2 className="text-2xl font-display font-semibold mb-8 text-primary-700 dark:text-primary-300">Easy Delivery</h2>
        <nav className="space-y-3">
          {sidebarItems.map((item) => (
            <button
              key={item.label}
              onClick={item.onClick}
              className={`sidebar-item ${window.location.pathname === '/' + item.label.toLowerCase().replace(' ', '-') ? 'active' : ''}`}
            >
              <span className="text-xl">{item.icon}</span>
              <span className="font-medium">{item.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6">
        {/* Role-based Header */}
        <div className="bg-primary-700 dark:bg-primary-900 rounded-2xl p-6 mb-8 shadow-xl flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-display font-semibold text-white mb-1">
              Welcome back, {user ? user.name : 'User'}!
            </h1>
            <p className="text-primary-200 dark:text-primary-300 text-lg">
              {isSender && "Ready to send packages and track deliveries?"}
              {isTraveler && "Ready to post trips and help deliver packages?"}
            </p>
          </div>
          <div className="flex items-center space-x-4">
            {isSender && (
              <button
                className="btn-primary flex items-center"
                onClick={() => navigate('/new-delivery')}
              >
                <FaPlus className="mr-2" />
                New Delivery
              </button>
            )}
            {isTraveler && (
              <button
                className="btn-primary flex items-center"
                onClick={() => navigate('/post-trip')}
              >
                <FaPlane className="mr-2" />
                Post Trip
              </button>
            )}
            <button
              className="btn-secondary flex items-center"
              onClick={() => navigate('/delivery-tracking')}
              title="Track Delivery"
            >
              <FaRoute className="mr-2" />
              Track Delivery
            </button>
            <button
              className="btn-secondary flex items-center"
              onClick={() => {
                localStorage.removeItem('token');
                localStorage.removeItem('user');
                navigate('/login');
              }}
              title="Logout"
            >
              <FaSignOutAlt className="mr-2" />
              Logout
            </button>
            <div className="relative">
              <input
                type="text"
                placeholder="Search..."
                className="input-field pl-10"
              />
              <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400 dark:text-secondary-600" />
            </div>
            <ThemeToggle />
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="stat-card">
            <div className="flex items-center">
              <FaBox className="text-primary-500 text-3xl mr-4" />
              <div>
                <p className="text-secondary-600 dark:text-secondary-400 uppercase tracking-wide font-semibold">Total Requests</p>
                <p className="text-4xl font-display font-bold">{totalRequests}</p>
              </div>
            </div>
          </div>
          <div className="stat-card">
            <div className="flex items-center">
              <FaClock className="text-warning-500 text-3xl mr-4" />
              <div>
                <p className="text-secondary-600 dark:text-secondary-400 uppercase tracking-wide font-semibold">Pending</p>
                <p className="text-4xl font-display font-bold">{pending}</p>
              </div>
            </div>
          </div>
          <div className="stat-card">
            <div className="flex items-center">
              <FaTruck className="text-accent-500 text-3xl mr-4" />
              <div>
                <p className="text-secondary-600 dark:text-secondary-400 uppercase tracking-wide font-semibold">In Transit</p>
                <p className="text-4xl font-display font-bold">{inTransit}</p>
              </div>
            </div>
          </div>
          <div className="stat-card">
            <div className="flex items-center">
              <FaBoxOpen className="text-success-500 text-3xl mr-4" />
              <div>
                <p className="text-secondary-600 dark:text-secondary-400 uppercase tracking-wide font-semibold">Delivered</p>
                <p className="text-4xl font-display font-bold">{delivered}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Deliveries/Trips */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {isSender && (
            <div className="card animate-fade-in">
              <h2 className="text-2xl font-display font-semibold mb-6 text-primary-700 dark:text-primary-300">Recent Deliveries</h2>
              {loading ? (
                <p className="text-secondary-600 dark:text-secondary-400">Loading...</p>
              ) : deliveryRequests.length === 0 ? (
                <p className="text-secondary-600 dark:text-secondary-400">No delivery requests yet.</p>
              ) : (
                <div className="space-y-4">
                  {deliveryRequests.slice(0, 5).map((req) => (
                    <div key={req._id} className="flex items-center justify-between p-4 bg-secondary-50 dark:bg-secondary-900 rounded-xl shadow-soft hover:shadow-medium transition-shadow duration-300">
                      <div>
                        <p className="font-semibold text-primary-700 dark:text-primary-300">{req.title}</p>
                        <p className="text-sm text-secondary-600 dark:text-secondary-400">{req.from} → {req.to}</p>
                      </div>
                      <span className={`status-badge ${
                        req.status === 'Delivered' ? 'status-delivered' :
                        req.status === 'In Transit' ? 'status-in-transit' :
                        'status-pending'
                      }`}>
                        {req.status}
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {isTraveler && (
            <div className="card animate-fade-in">
              <h2 className="text-2xl font-display font-semibold mb-6 text-primary-700 dark:text-primary-300">Recent Trips</h2>
              {travelerTrips.length === 0 ? (
                <p className="text-secondary-600 dark:text-secondary-400">No trips posted yet.</p>
              ) : (
                <div className="space-y-4">
                  {travelerTrips.slice(0, 5).map((trip) => (
                    <div key={trip._id} className="flex items-center justify-between p-4 bg-secondary-50 dark:bg-secondary-900 rounded-xl shadow-soft hover:shadow-medium transition-shadow duration-300">
                      <div>
                        <p className="font-semibold text-primary-700 dark:text-primary-300">{trip.from} → {trip.to}</p>
                        <p className="text-sm text-secondary-600 dark:text-secondary-400">{new Date(trip.date).toLocaleDateString()}</p>
                      </div>
                      <span className="status-badge status-delivered">
                        Active
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>

        {/* AI Smart Match */}
        <AISmartMatch userId={user ? user.id : null} token={localStorage.getItem('token')} />

        {/* Notifications */}
        <Notifications />
      </div>
    </div>
  );
};

export default Dashboard;
