import React, { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const [trips, setTrips] = useState([]);
  const [fraudReports, setFraudReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const [usersRes, deliveriesRes, tripsRes, fraudReportsRes] = await Promise.all([
          fetch('http://localhost:5003/api/admin/users', {
            headers: { Authorization: `Bearer ${token}` },
          }),
          fetch('http://localhost:5003/api/admin/deliveries', {
            headers: { Authorization: `Bearer ${token}` },
          }),
          fetch('http://localhost:5003/api/admin/trips', {
            headers: { Authorization: `Bearer ${token}` },
          }),
          fetch('http://localhost:5003/api/admin/fraud-reports', {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        const usersData = await usersRes.json();
        const deliveriesData = await deliveriesRes.json();
        const tripsData = await tripsRes.json();
        const fraudReportsData = await fraudReportsRes.json();

        setUsers(usersData);
        setDeliveries(deliveriesData);
        setTrips(tripsData);
        setFraudReports(fraudReportsData);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleBanUser = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:5003/api/admin/users/${userId}/ban`, {
        method: 'PUT',
        headers: { Authorization: `Bearer ${token}` },
      });
      // Refresh users list
      const response = await fetch('http://localhost:5003/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdateFraudStatus = async (reportId, status) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:5003/api/admin/fraud-reports/${reportId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });
      // Refresh fraud reports list
      const response = await fetch('http://localhost:5003/api/admin/fraud-reports', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setFraudReports(data);
    } catch (error) {
      console.error(error);
    }
  };

  if (loading) {
    return <p>Loading admin dashboard...</p>;
  }

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold">Total Users</h3>
          <p className="text-2xl font-bold">{users.length}</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold">Total Deliveries</h3>
          <p className="text-2xl font-bold">{deliveries.length}</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold">Active Trips</h3>
          <p className="text-2xl font-bold">{trips.filter(trip => trip.status === 'Active').length}</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold">Pending Fraud Reports</h3>
          <p className="text-2xl font-bold">{fraudReports.filter(report => report.status === 'Pending').length}</p>
        </div>
      </div>

      <div className="bg-white p-6 rounded shadow mb-6">
        <h2 className="text-xl font-bold mb-4">Users</h2>
        <table className="w-full">
          <thead>
            <tr className="border-b">
              <th className="text-left p-2">Name</th>
              <th className="text-left p-2">Email</th>
              <th className="text-left p-2">Role</th>
              <th className="text-left p-2">Status</th>
              <th className="text-left p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id} className="border-b">
                <td className="p-2">{user.name}</td>
                <td className="p-2">{user.email}</td>
                <td className="p-2">{user.role}</td>
                <td className="p-2">{user.isBanned ? 'Banned' : 'Active'}</td>
                <td className="p-2">
                  {!user.isBanned && (
                    <button
                      onClick={() => handleBanUser(user._id)}
                      className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                    >
                      Ban
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="bg-white p-6 rounded shadow mb-6">
        <h2 className="text-xl font-bold mb-4">Recent Deliveries</h2>
        <table className="w-full">
          <thead>
            <tr className="border-b">
              <th className="text-left p-2">Title</th>
              <th className="text-left p-2">From</th>
              <th className="text-left p-2">To</th>
              <th className="text-left p-2">Status</th>
              <th className="text-left p-2">Date</th>
            </tr>
          </thead>
          <tbody>
            {deliveries.slice(0, 10).map((delivery) => (
              <tr key={delivery._id} className="border-b">
                <td className="p-2">{delivery.title}</td>
                <td className="p-2">{delivery.from}</td>
                <td className="p-2">{delivery.to}</td>
                <td className="p-2">{delivery.status}</td>
                <td className="p-2">{new Date(delivery.createdAt).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-bold mb-4">Fraud Reports</h2>
        {fraudReports.length === 0 ? (
          <p>No fraud reports found.</p>
        ) : (
          <table className="w-full">
            <thead>
              <tr className="border-b">
                <th className="text-left p-2">Reported By</th>
                <th className="text-left p-2">Delivery Title</th>
                <th className="text-left p-2">Description</th>
                <th className="text-left p-2">Status</th>
                <th className="text-left p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {fraudReports.map((report) => (
                <tr key={report._id} className="border-b">
                  <td className="p-2">{report.reportedBy.name}</td>
                  <td className="p-2">{report.delivery.title}</td>
                  <td className="p-2">{report.description}</td>
                  <td className="p-2">{report.status}</td>
                  <td className="p-2">
                    {report.status === 'Pending' && (
                      <>
                        <button
                          onClick={() => handleUpdateFraudStatus(report._id, 'Reviewed')}
                          className="bg-yellow-500 text-white px-3 py-1 rounded mr-2 hover:bg-yellow-600"
                        >
                          Mark Reviewed
                        </button>
                        <button
                          onClick={() => handleUpdateFraudStatus(report._id, 'Resolved')}
                          className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                        >
                          Mark Resolved
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};
