import React, { useEffect, useState } from 'react';

const Wallet = () => {
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWallet = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5003/api/wallet', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error('Failed to fetch wallet');
        }
        const data = await response.json();
        setWallet(data);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };
    fetchWallet();
  }, []);

  if (loading) {
    return <p>Loading wallet...</p>;
  }

  if (!wallet) {
    return <p>Wallet not found.</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">My Wallet</h2>
      <div className="bg-white p-6 rounded shadow mb-6">
        <h3 className="text-lg font-semibold mb-2">Balance</h3>
        <p className="text-2xl font-bold text-green-600">${wallet.balance}</p>
        <p className="text-gray-600">Reward Points: {wallet.rewardPoints}</p>
      </div>
      <div className="bg-white p-6 rounded shadow">
        <h3 className="text-lg font-semibold mb-4">Transaction History</h3>
        {wallet.transactions.length === 0 ? (
          <p>No transactions found.</p>
        ) : (
          <ul>
            {wallet.transactions.map((transaction) => (
              <li key={transaction._id} className="border-b py-2">
                <p>{transaction.description}</p>
                <p className={`font-semibold ${transaction.type === 'credit' ? 'text-green-600' : 'text-red-600'}`}>
                  {transaction.type === 'credit' ? '+' : '-'}${transaction.amount}
                </p>
                <p className="text-sm text-gray-500">{new Date(transaction.createdAt).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Wallet;
