import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBox, FaTruck, FaBoxOpen, FaClock, FaPlane, FaMapMarkerAlt, FaPlus, FaWallet, FaExclamationTriangle, FaStar } from 'react-icons/fa';
import AISmartMatch from '../components/AISmartMatch';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [deliveryRequests, setDeliveryRequests] = useState([]);
  const [travelerTrips, setTravelerTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(storedUser));
    fetchDeliveryRequests();
    if (JSON.parse(storedUser).role === 'Traveler') {
      fetchTravelerTrips();
    }
  }, []);

  const fetchDeliveryRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/delivery', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch delivery requests');
      }
      const data = await response.json();
      setDeliveryRequests(data);
    } catch (error) {
      console.error(error);
      setDeliveryRequests([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchTravelerTrips = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5003/api/traveler', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch traveler trips');
      }
      const data = await response.json();
      setTravelerTrips(data);
    } catch (error) {
      console.error(error);
    }
  };

  const countByStatus = (status) => {
    return deliveryRequests.filter((req) => req.status === status).length;
  };

  const totalRequests = deliveryRequests.length;
  const inTransit = countByStatus('In Transit');
  const delivered = countByStatus('Delivered');
  const pending = countByStatus('Pending');

  const isTraveler = user && user.role === 'Traveler';
  const isSender = user && user.role === 'Sender';

  // Sidebar navigation items
  const sidebarItems = [
    { label: 'Dashboard', icon: <FaBox />, onClick: () => navigate('/dashboard') },
    { label: 'Wallet', icon: <FaWallet />, onClick: () => navigate('/wallet') },
    { label: 'Reviews', icon: <FaStar />, onClick: () => navigate('/reviews/' + (user ? user.id : '')) },
    { label: 'Fraud Reports', icon: <FaExclamationTriangle />, onClick: () => navigate('/admin/fraud-reports') },
  ];

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-md p-6">
        <h2 className="text-xl font-bold mb-6">Easy Delivery</h2>
        <nav className="space-y-4">
          {sidebarItems.map((item) => (
            <button
              key={item.label}
              onClick={item.onClick}
              className="flex items-center space-x-3 w-full text-left px-3 py-2 rounded hover:bg-gray-100"
            >
              <span className="text-lg">{item.icon}</span>
              <span>{item.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6">
        {/* Role-based Header */}
        <div className="bg-blue-700 text-white rounded-lg p-6 mb-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold">
                Welcome back, {user ? user.name : 'User'}!
              </h1>
              <p>
                {isSender && "Ready to send packages and track deliveries?"}
                {isTraveler && "Ready to post trips and help deliver packages?"}
              </p>
            </div>
            <div className="flex space-x-3">
              {isSender && (
                <button
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded flex items-center"
                  onClick={() => navigate('/new-delivery')}
                >
                  <FaPlus className="mr-2" />
                  New Delivery
                </button>
              )}
              {isTraveler && (
                <button
                  className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded flex items-center"
                  onClick={() => navigate('/post-trip')}
                >
                  <FaPlane className="mr-2" />
                  Post Trip
                </button>
              )}
              <button
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded flex items-center"
                onClick={() => {
                  localStorage.removeItem('token');
                  localStorage.removeItem('user');
                  navigate('/login');
                }}
              >
                Logout
              </button>
              <input
                type="text"
                placeholder="Search..."
                className="p-2 rounded text-black"
              />
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-4 gap-4 mb-6">
          {isSender && (
            <>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaBoxOpen className="text-blue-500 text-3xl" />
                <div>
                  <p className="font-semibold">My Requests</p>
                  <p className="text-xl">{totalRequests}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaTruck className="text-yellow-600 text-3xl" />
                <div>
                  <p className="font-semibold">In Transit</p>
                  <p className="text-xl">{inTransit}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaBox className="text-green-600 text-3xl" />
                <div>
                  <p className="font-semibold">Delivered</p>
                  <p className="text-xl">{delivered}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaClock className="text-orange-500 text-3xl" />
                <div>
                  <p className="font-semibold">Pending</p>
                  <p className="text-xl">{pending}</p>
                </div>
              </div>
            </>
          )}
          {isTraveler && (
            <>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaPlane className="text-blue-500 text-3xl" />
                <div>
                  <p className="font-semibold">My Trips</p>
                  <p className="text-xl">{travelerTrips.length}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaBoxOpen className="text-green-600 text-3xl" />
                <div>
                  <p className="font-semibold">Available Parcels</p>
                  <p className="text-xl">{totalRequests}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaTruck className="text-yellow-600 text-3xl" />
                <div>
                  <p className="font-semibold">Active Deliveries</p>
                  <p className="text-xl">{inTransit}</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-4 flex items-center space-x-4 shadow">
                <FaClock className="text-orange-500 text-3xl" />
                <div>
                  <p className="font-semibold">Completed</p>
                  <p className="text-xl">{delivered}</p>
                </div>
              </div>
            </>
          )}
        </div>

        {/* Role-based Content */}
        {isSender && (
          <div className="bg-white rounded-lg p-6 shadow">
            <div className="flex justify-between items-center mb-4">
              <h2 className="font-bold text-lg">My Delivery Requests</h2>
              <button
                className="border border-gray-300 rounded px-3 py-1 hover:bg-gray-100"
                onClick={() => navigate('/deliveries')}
              >
                View All
              </button>
            </div>
            {loading ? (
              <p>Loading...</p>
            ) : deliveryRequests.length === 0 ? (
              <p>No delivery requests found.</p>
            ) : (
              deliveryRequests.slice(0, 3).map((req) => (
                <div
                  key={req._id}
                  className="border border-gray-200 rounded p-4 mb-3 flex justify-between items-center"
                >
                  <div>
                    <p className="font-semibold">{req.title}</p>
                    <p className="text-sm text-gray-600">
                      {req.from} → {req.to}
                    </p>
                    <p className="text-xs text-gray-500">
                      Deadline: {new Date(req.deadline).toLocaleDateString()} • {req.weight} kg • ${req.cost}
                    </p>
                  </div>
                  <div>
                    <span
                      className={`px-3 py-1 rounded text-sm font-semibold ${
                        req.status === 'Delivered'
                          ? 'bg-green-100 text-green-700'
                          : req.status === 'In Transit'
                          ? 'bg-blue-100 text-blue-700'
                          : 'bg-yellow-100 text-yellow-700'
                      }`}
                    >
                      {req.status}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {isTraveler && (
          <div className="space-y-6">
            {/* My Trips Section */}
            <div className="bg-white rounded-lg p-6 shadow">
              <div className="flex justify-between items-center mb-4">
                <h2 className="font-bold text-lg">My Posted Trips</h2>
                <button
                  className="border border-gray-300 rounded px-3 py-1 hover:bg-gray-100"
                  onClick={() => navigate('/post-trip')}
                >
                  Post New Trip
                </button>
              </div>
              {travelerTrips.length === 0 ? (
                <p>No trips posted yet.</p>
              ) : (
                travelerTrips.slice(0, 2).map((trip) => (
                  <div
                    key={trip._id}
                    className="border border-gray-200 rounded p-4 mb-3 flex justify-between items-center"
                  >
                    <div>
                      <p className="font-semibold">{trip.from} → {trip.to}</p>
                      <p className="text-sm text-gray-600">
                        Date: {new Date(trip.date).toLocaleDateString()} • Max: {trip.maxWeight}kg • ${trip.costPerKg}/kg
                      </p>
                    </div>
                    <div>
                      <span
                        className={`px-3 py-1 rounded text-sm font-semibold ${
                          trip.status === 'Active'
                            ? 'bg-green-100 text-green-700'
                            : 'bg-gray-100 text-gray-700'
                        }`}
                      >
                        {trip.status}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>

            {/* Available Parcels Section */}
            <div className="bg-white rounded-lg p-6 shadow">
              <div className="flex justify-between items-center mb-4">
                <h2 className="font-bold text-lg">Available Parcels</h2>
                <button
                  className="border border-gray-300 rounded px-3 py-1 hover:bg-gray-100"
                  onClick={() => navigate('/deliveries')}
                >
                  View All
                </button>
              </div>
              {loading ? (
                <p>Loading...</p>
              ) : deliveryRequests.length === 0 ? (
                <p>No parcels available.</p>
              ) : (
                deliveryRequests.slice(0, 3).map((req) => (
                  <div
                    key={req._id}
                    className="border border-gray-200 rounded p-4 mb-3 flex justify-between items-center"
                  >
                    <div>
                      <p className="font-semibold">{req.title}</p>
                      <p className="text-sm text-gray-600">
                        {req.from} → {req.to}
                      </p>
                      <p className="text-xs text-gray-500">
                        Deadline: {new Date(req.deadline).toLocaleDateString()} • {req.weight} kg • ${req.cost}
                      </p>
                    </div>
                    <div>
                      <span className="px-3 py-1 rounded text-sm font-semibold bg-blue-100 text-blue-700">
                        Available
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {/* AI Smart Match Feature */}
        {(isSender || isTraveler) && user && (
          <AISmartMatch userId={user.id} token={localStorage.getItem('token')} />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
