import React, { useEffect, useState } from 'react';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    // Fetch existing reviews (dummy data for now)
    setReviews([
      { id: 1, name: 'Alice', rating: 5, comment: 'Great service!' },
      { id: 2, name: 'Bob', rating: 4, comment: 'Reliable and fast.' },
      { id: 3, name: 'Charlie', rating: 3, comment: 'Good, but can improve.' },
    ]);
  }, []);

  const handleAddReview = () => {
    if (!newReview.trim()) return;
    const review = {
      id: reviews.length + 1,
      name: user ? user.name : 'Anonymous',
      rating: 5,
      comment: newReview.trim(),
    };
    setReviews([review, ...reviews]);
    setNewReview('');
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Customer Reviews</h1>
      <div className="mb-6">
        <textarea
          rows="4"
          placeholder="Write your review here..."
          value={newReview}
          onChange={(e) => setNewReview(e.target.value)}
          className="w-full p-3 border rounded"
        />
        <button
          onClick={handleAddReview}
          className="mt-3 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit Review
        </button>
      </div>
      {reviews.length === 0 ? (
        <p>No reviews yet.</p>
      ) : (
        <ul className="space-y-4">
          {reviews.map((review) => (
            <li key={review.id} className="border p-4 rounded shadow">
              <p className="font-semibold">{review.name}</p>
              <p>Rating: {review.rating} / 5</p>
              <p>{review.comment}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Reviews;
